const fs = require("fs");
const path = require("path");
const Finder = require("../../page/finder");

class ElectronFinderExtention extends Finder {

  /**
   * Attach the file given with filePath.
   * Rather than setting it to the files attribute of the specified input element,
   * set it as the data-file-object attribute of the input element.
   * This is because the files attribute can not be rewritten by script
   * @param {string} filePath - Specify the path of the file to be attached
   * @param {object} options
   * @param {object} options.fileType - type attribute of the File (see HTML5 File API)
   * @param {object} options.replaceSubmitWithAjax - whether replace form-submit with ajax request, default is true.
   * @return {Promise}
   */
  attachFile(filePath, options = {}){
    const fileType = options.fileType || "";
    options.replaceSubmitWithAjax = options.replaceSubmitWithAjax || true;

    return (options.replaceSubmitWithAjax ? this.clone().replaceSubmitWithAjax() : Promise.resove())
    .then(()=>{
      return this.executeJs(`(elements)=>{
        const input = elements[0];
        browserSocket.once('file-body-transfer', (event, data)=>{
          input.attributes["data-file-object"] = new File([data], '${path.basename(filePath)}', { type: "${fileType}" });
          browserSocket.send('file-body-transfer-end', 'done');
        });
      }`);
    })
    .then(()=>{
      let data = fs.readFileSync(filePath);
      return this.page.waitForBrowserSocket('file-body-transfer-end', ()=>{
        this.page.browserPage.nativeObject.send('file-body-transfer', data);
      })
    });
  }
  /**
   * Replace the behavior when form is submitted with ajax. And The behavior is below,
   * Send the request by including the File object specified in the data-file-object attribute of
   * the input element of type=file in the form.
   * When submit request get response 200:OK, call onSuccess callback with response object,
   * by default replace document.body by response.text() and message send with browserSocket on channel 'submit-ajax-done'.
   * When submit request get response 302:Redirect, call onRedirect callback with response object,
   * by default load the page of response.url, and pageLoaded event occur.
   * When submit request get response neither 200:OK or 302:Redirect, call onRespoinse callback with response object,
   * by default throw Error
   * @param {object} callbacks - An object with callback with each attribute name
   * @param {function,string} callbacks.onSuccess - callback function for handling response when status 200.
   * @param {function,string} callbacks.onRedirect - callback function for handling response when status 302.
   * @param {function,string} callbacks.onResponse - callback function for handling response when status is not 200 and 302.
   * @return {Promise}
   */
  replaceSubmitWithAjax(callbacks = {}){
    const callbacksDefault = {
      onSuccess: (response)=>{ response.text().then((text)=>{ document.body.innerHTML = text; browserSocket.send('submit-ajax-done', 'done'); }) },
      onRedirect: (response)=>{ window.location.href = response.url },
      onResponse: (response)=>{ throw new Error(response.status + " " + response.statusText) }
    };
    callbacks.onSuccess  = callbacks.onSuccess  || callbacksDefault.onSuccess;
    callbacks.onRedirect = callbacks.onRedirect || callbacksDefault.onRedirect;
    callbacks.onResponse = callbacks.onResponse || callbacksDefault.onResponse;

    return this.executeJs(`(elements)=>{
        let searchTag = (el, tag)=>{ return el == null || el.tagName == tag ? el : searchTag(el.parentElement, tag) };
        let form = searchTag(elements[0].parentElement, "FORM");
        if(!form || form.attributes["data-upload-file-event-listener-added"]) return;

        form.attributes["data-upload-file-event-listener-added"] = true;
        form.addEventListener('submit', (event)=>{
          event.preventDefault();

          const formData = new FormData(form);
          for(let i = 0; i < form.elements.length; i++){
            let input = form.elements[i];
            if(input && input.type == "file" && input.attributes["data-file-object"]){
              formData.append(input.name, input.attributes["data-file-object"]);
            }
          }
          fetch(form.action, {
            method: form.method, body: formData, credentials: 'same-origin',
          })
          .then((response)=>{
            const callbacks = {
              redirect: ${callbacks.onRedirect.toString()},
              success: ${callbacks.onSuccess.toString()},
              response: ${callbacks.onResponse.toString()}
            }
            switch(response.status){
              case 200: return response.redirected ? callbacks.redirect(response) : callbacks.success(response);
              case 302: return callbacks.redirect(response);
              default:  return callbacks.response(response);
            }
          })
        })
      }`
    );
  }
}

module.exports = ElectronFinderExtention;

const path = require('path');
const {ipcMain} = require('electron');
const BrowserPage = require("../interface/browser_page");
const BrowserSocket = require("../interface/browser_socket");
const ElectronFinderExtention = require("./electron_finder_extention");
const {config} = require("../../utils/config");
const DateFormat = require('../../utils/date_format');

const TIMEOUT_MSEC = config.mocha.timeout;

class ElectronPage extends BrowserPage {
  constructor(window){
    super();
    this._window = window;

    this._window.on('ready', ()=>{
      this.nativeObject.on('new-window', (event, url, frameName)=>{
        this.emit(BrowserPage.Events.NewWindow, frameName);
      })
    })
  }
  get window(){
    return this._window;
  }
  get nativeObject(){
    return this._window.browserWindow.webContents;
  }
  on(eventName, callback){
    this._addEventListener(eventName, callback, fasle)
  }
  once(eventName, callback){
    this._addEventListener(eventName, callback, true)
  }
  _addEventListener(eventName, callback, once = true){
    if(eventName in ElectronPage.EventMappings){
      this.nativeObject[once ? 'once' : 'on'](ElectronPage.EventMappings[eventName], callback)
    }else{
      super[once ? 'once' : 'on'](evnetName, callback)
    }
  }
  removeAllListeners(eventName){
    if(eventName in ElectronPage.EventMappings){
      this.nativeObject.removeAllListeners(ElectronPage.EventMappings[eventName])
    }else{
      super.removeAllListeners(eventName);
    }
  }
  get socket(){
    this._socket = this._socket || new ElectronSocket();
    return this._socket;
  }
  get FinderClass(){
    return ElectronFinderExtention;
  }
  loadURL(url, options = {}){
    return this.nativeObject.loadURL(url)
  }
  getURL(){
    return this.nativeObject.getURL();
  }
  executeJavaScript(code, ...args){
    return this.nativeObject.executeJavaScript(code, ...args);
  }
  openDevTools(){
    return this.nativeObject.openDevTools();
  }
  waitForDownload(action, options){
    return new Promise((resolve, reject)=>{
      let timeoutId = setTimeout(()=>{ reject(new Error('timeout')) }, TIMEOUT_MSEC);

      this.nativeObject.session.once('will-download', (event, item, webContents) => {
        const result = { filename: item.getFilename() };
        // Set the save path, making Electron not to prompt a save dialog.
        const saveFilename = `${DateFormat.toTimepstampString(new Date())}_${item.getFilename()}`;
        result.savedFilePath = path.resolve(path.join(config.fileDownloadDir, saveFilename));

        item.setSavePath(result.savedFilePath);
        item.on('updated', (event, state)=>{
          if(state === 'interrupted') reject(new Error('Download is interrupted'));
        })
        item.once('done', (event, state) => {
          clearTimeout(timeoutId);
          if(state == 'completed'){
            resolve(result)
          }else{
            reject(new Error(`Download failed: ${state}`))
          }
        })
      })
      action();
    });
  }
  waitForConfirm(action, message = undefined, isClickOK = false){
    return new Promise((resolve, reject)=>{
      const channel = 'confirm-synchronous';
      let timeoutId = setTimeout(()=>{ reject(new Error('timeout')) }, TIMEOUT_MSEC);
      this.socket.once(channel, (arg, event)=>{
        clearTimeout(timeoutId);
        if(message && message != arg){
          return reject(new Error(`expected message is "${message}", but actual is "${arg}"`));
        }
        event.returnValue = isClickOK;
        resolve(message);
      });
      return this.executeJavaScript(`{
        let __original_confirm = window.confirm;
        window.confirm = function(message){
          var ret = browserSocket.sendSync('${channel}', message);
          window.confirm = __original_confirm;
          return ret;
        };
      }`, true)
      .then(()=>{ return action && action() })
      .catch(reject)
    });
  }
}

class ElectronSocket extends BrowserSocket {
  on(channel, callback){
    return ipcMain.on(channel, (event, arg)=>{ callback(arg, event) });
  }
  once(channel, callback){
    return ipcMain.once(channel, (event, arg)=>{ callback(arg, event) });
  }
  removeAllListeners(channel){
    return ipcMain.removeAllListeners(channel);
  }
  send(channel, ...args){ throw new Error("Not Implemented") }
}

ElectronPage.EventMappings = {
  [BrowserPage.Events.Load]: 'did-finish-load',
  [BrowserPage.Events.LoadError]: 'did-fail-load',
}

module.exports = ElectronPage;

const electron = require('electron');
const {BrowserWindow} = electron;
const Browser = require('../interface/browser');
const ElectronPage = require('./electron_page');

class ElectronBrowser extends Browser {
  constructor(options){
    super();
    this._app = electron.app;
    this._app.on('window-all-closed', ()=>{ this._app.quit() });
    this._app.on('ready', (...args)=>{ this.emit(Browser.Events.Ready, ...args) });
  }
  isReady(){
    return this._app.isReady();
  }
  exit(...args){
    return this._app.exit(...args)
  }
  /**
   * @return {Promise<object>} - object is browserWindow object provided by each browser.
   */
  createBrowserWindow(windowOptions, callbacks){
    const browserWindow = new BrowserWindow(windowOptions);

    browserWindow.webContents.on('context-menu', (e, params)=>{
      browserWindow.webContents.inspectElement(params.x, params.y);
    })
    browserWindow.webContents.on('new-window', (event, url, frameName, disposition, options, additionalFeatures)=>{
      event.preventDefault();
      let newWindow = callbacks['new-window'](frameName, undefined, options);
      if(url && url != "about:blank"){
        newWindow.page.load(url)
      }
      event.newGuest = newWindow.browserWindow
    })
    const browserWindowId = browserWindow.id; // on closed event, browserWindow object has been destroyed, so could not get "id";
    browserWindow.on('closed', (event)=>{
      callbacks['closed'](browserWindowId);
    });
    return Promise.resolve({ id: browserWindow.id, browserWindow: browserWindow });
  }
  /**
   * Get a browserWindow object by id if browserWindow given the id exist.
   * @return {Promise<object>} - object is browserWindow object provided by each browser.
   */
  getBrowserWindowById(id){
    return Promise.resolve(id && BrowserWindow.fromId(parseInt(id)));
  }

  createBrowserPage(window){
    return new ElectronPage(window);
  }
}

module.exports = ElectronBrowser;

