const puppeteer = require('puppeteer');
const Browser = require('../interface/browser');
const PuppeteerPage = require('./puppeteer_page');

class PuppeteerBrowser extends Browser {
  constructor(options = {}){
    super();
    puppeteer.launch(options).then((browser)=>{
      this._browser = browser;
      this.emit(Browser.Events.Ready);
    })
    this.on(Browser.Events.Ready, ()=>{ this._ready = true });
  }
  isReady(){
    return !!this._ready;
  }
  exit(...args){
    this._browser && this._browser.close();
    process.exit(...args);
  }
  createBrowserWindow(windowOptions, callbacks){
    return new Promise((resolve, reject)=>{
      if(this.isReady()){
        this._browser.newPage().then(resolve);
      }
      this.on(Browser.Events.Ready, ()=>{
        this._browser.newPage().then(resolve);
      })
    })
    .then((browserPage)=>{
      this._browserPage = browserPage;
      this._browser.on('targetcreated', (target)=>{
        if(target.type() != 'page') return;
        target.page().then((page)=>{
          return page.evaluate(()=>{ return window.name })
        })
        .then((frameName)=>{
          callbacks['new-window'](frameName, target._targetInfo.targetId, {});
        })
      })
      this._browser.on('targetdestroyed', (target)=>{
        if(target.type() != 'page') return;
        callbacks['closed'](target._targetInfo.targetId)
      })
      return { id: browserPage._client.targetId(), browserWindow: browserPage };
    })
  }
  /**
   * Get a browserWindow object by id if browserWindow given the id exist.
   * @return {Promise<object>} - object is browserWindow object provided by each browser.
   */
  getBrowserWindowById(id){
    if(id && this._browser){
      return this._browser.pages()
      .then((pages)=>{
        return pages.filter(page => page._client.targetId() == id)[0];
      })
    }else{
      return Promise.resolve()
    }
  }
  createBrowserPage(window){
    return new PuppeteerPage(window);
  }
}

module.exports = PuppeteerBrowser;

const fs = require("fs");
const path = require("path");
const Finder = require("../../page/finder");

class PuppeteerFinderExtention extends Finder {

  /**
   * Attach the file given with filePath.
   * Now, it is not supported that you attach a file to input-elements in iframe .
   * @param {string} filePath - Specify the path of the file to be attached
   * @param {object} options
   * @return {Promise}
   */
  attachFile(filePath, options = {}){
    const firstSelector = this.conditions[0].constructor.name == "Selector" ? this.conditions[0].selector : "*";

    return this.page.browserPage.nativeObject.$$(firstSelector)
    .then((elementHandles)=>{
      return this.conditions.reduce((prev, condition, i)=>{
        return prev.then((elementHandles)=>{
          if(condition.constructor.name == "Selector"){
            return i == 0 ? elementHandles : elementHandles.$$(condition.selector);
          }
          if(condition.constructor.name == "Filter"){
            return elementHandles.filter(eval(condition.code))
          }
        })
      }, Promise.resolve(elementHandles));
    })
    .then((elementHandles)=>{
      if(elementHandles.length == 0) return;//throw new Error('input element to attach file is not found')

      return elementHandles[0].uploadFile(filePath);
    })
  }
}

module.exports = PuppeteerFinderExtention;

const path = require('path');
const fs = require('fs');
const EventEmitter = require('events');
const BrowserPage = require("../interface/browser_page");
const BrowserSocket = require("../interface/browser_socket");
const PuppeteerFinderExtention = require("./puppeteer_finder_extention");
const {config} = require("../../utils/config");
const DateFormat = require('../../utils/date_format');

const TIMEOUT_MSEC = config.mocha.timeout;
const DOWNLOAD_PATH = path.resolve(config.fileDownloadDir);

class PuppeteerPage extends BrowserPage {
  constructor(window){
    super();
    this._window = window;
    const browserSocket = this._browserSocket = new PuppeteerSocket();

    this._window.on('ready', ()=>{
      this.nativeObject.exposeFunction('invokeBrowserSocket', (...args)=>{
        const argArray = JSON.parse(args.join(""))
        return browserSocket[argArray[0]](...(argArray.slice(1)));
      })
      this.nativeObject.evaluateOnNewDocument(()=>{
        window.browserSocket = {
          on: (...args)=>{ window.invokeBrowserSocket('on', ...args) },
          once: (...args)=>{ window.invokeBrowserSocket('once', ...args) },
          removeAllListeners: (...args)=>{ window.invokeBrowserSocket('removeAllListeners', ...args) },
          send: (...args)=>{ window.invokeBrowserSocket('send', ...args) },
        }
      });
      this._setDownloadPath(DOWNLOAD_PATH);
    })
  }
  on(eventName, callback){
    this._addEventListener(eventName, callback, fasle)
  }
  once(eventName, callback){
    this._addEventListener(eventName, callback, true)
  }
  _addEventListener(eventName, callback, once = true){
    if(eventName in PuppeteerPage.EventMappings){
      this.nativeObject[once ? 'once' : 'on'](PuppeteerPage.EventMappings[eventName], callback)
    }else{
      super[once ? 'once' : 'on'](evnetName, callback)
    }
  }
  removeAllListeners(eventName){
    if(eventName in PuppeteerPage.EventMappings){
      this.nativeObject.removeAllListeners(PuppeteerPage.EventMappings[eventName])
    }else{
      super.removeAllListeners(eventName);
    }
  }
  /**
   * @return {Window}
   */
  get window(){
    return this._window;
  }
  /**
   * @return {object}
   */
  get nativeObject(){
    return this._window.browserWindow;
  }
  /**
   * @return {BrowserSocket}
   */
  get socket(){
    return this._browserSocket;
  }
  /**
   * @return {Finder}
   */
  get FinderClass(){
    return PuppeteerFinderExtention;
  }
  /**
   * @return {Promise<BrowserPage>}
   */
  loadURL(url, options = {}){
    return this.nativeObject.goto(url);
  }
  /**
   * @return {string}
   */
  getURL(){
    return this.nativeObject.url();
  }
  /**
   * @return {Promise<string|number|boolean>}
   */
  executeJavaScript(code, ...args){
    return this.nativeObject.evaluate(code, ...args);
  }
  /**
   * show developer tool
   */
  openDevTools(){ /*ignoe*/ }
  /**
   * @private
   */
  _setDownloadPath(downloadPath){
    return this.nativeObject._client.send(
      'Page.setDownloadBehavior', {behavior: 'allow', downloadPath: downloadPath }
    );
  }
  /**
   * @return {Promise<object>}
   */
  waitForDownload(action, options){
    const isDownloadComplete = (path)=>{
      return new Promise((resolve, reject)=>{
        fs.readdir(path, (err, files)=>{ err ? reject(err) : resolve(files) })
      })
      .then((files)=>{
        if (files.length === 0) return false;
        // .crdownload
        const nowDownloading = Array.from(files).some(file => /.*\.crdownload$/.test(file))
        return !nowDownloading
      });
    };

    const waitResponse = ()=>{
      return new Promise((resolve, reject)=>{
        let timeoutId = setTimeout(()=>{ reject(new Error('timeout')) }, TIMEOUT_MSEC);
        this.nativeObject.removeAllListeners('response');
        this.nativeObject.on('response', (response)=>{
          let contentDisposition = response.headers["content-disposition"];
          if(contentDisposition){
            clearTimeout(timeoutId);
            let matched = contentDisposition.match(/filename=\"(.*)\"$/);
            resolve(matched ? matched[1] : "");
          }
        })
        action();
      })
    };
    const downloadPath = options.downloadPath || DOWNLOAD_PATH;
    const waitTimeSpanMs = 100;

    return (options.downloadPath ? this._setDownloadPath(options.downloadPath) : Promise.resolve())
// patch start:
// page.on('response') do not happen in puppeteer v0.13, so check saved file.
//    .then(waitResponse)
//    .then((filename)=>{
    .then(()=>{
      return new Promise((resolve, reject)=>{
        fs.readdir(downloadPath, (err, files)=>{ err ? reject(err) : resolve(files) })
      })
    })
    .then((filenames)=>{
      action();
// patch end
      return new Promise((resolve, reject)=>{
        let totalWaitTimeMs = 0;
        const waitAndCheck = ()=>{
          if(totalWaitTimeMs > TIMEOUT_MSEC) return reject(new Error('timeout'));

          setTimeout(()=>{
            totalWaitTimeMs += waitTimeSpanMs;
            isDownloadComplete(downloadPath).catch(reject).then((completed)=>{
              if(!completed) return waitAndCheck();

// patch start
              return new Promise((resolve, reject)=>{
                const findDownloadedFile = ()=>{
                  fs.readdir(downloadPath, (err, files)=>{
                    if(err) return reject(err);
                    let filename = files.filter(file => !filenames.includes(file))[0];
                    if(!filename) return findDownloadedFile();
                    resolve(filename);
                  })
                };
                findDownloadedFile();
              })
              .then((filename)=>{
// patch end
                resolve({ filename: filename, savedFilePath: path.resolve(path.join(downloadPath, filename)) });
              });
            })
          }, waitTimeSpanMs);
        };
        waitAndCheck();
      });
    });
  }

  /**
   * @return {Promise<string>} resolved by confirm message
   */
  waitForConfirm(action, message = undefined, isClickOK = false){
    return new Promise((resolve, reject)=>{
      let timeoutId = setTimeout(()=>{ reject(new Error('timeout')) }, TIMEOUT_MSEC);
      this.nativeObject.once('dialog', (dialog)=>{
        if(dialog.type != 'confirm') return;

        clearTimeout(timeoutId);
        if(message && message != dialog.message()){
          return reject(new Error(`expected message is "${message}", but actual is "${dialog.message()}"`));
        }
        dialog.accept().then(resolve);
      })
      action();
    })
  }
}

class PuppeteerSocket extends BrowserSocket {
  constructor(){
    super();
    this.emitter = new EventEmitter();
  }
  on(channel, callback){
    return this.emitter.on(channel, callback);
  }
  once(channel, callback){
    return this.emitter.once(channel, callback);
  }
  removeAllListeners(channel){
    return this.emitter.removeAllListeners(channel);
  }
  send(channel, ...args){
    return this.emitter.emit(channel, ...args)
  }
}

PuppeteerPage.EventMappings = {
  [BrowserPage.Events.Load]: 'load',
  [BrowserPage.Events.LoadError]: 'requestfailed',
//  [BrowserPage.Events.NewWindow]: 'did-fail-load',
}

module.exports = PuppeteerPage;

const FinderBase = require('./finder_base');
const FinderAction = require('./finder_action');
const FinderFilter = require('./finder_filter');

/**
 * @extends {FinderBase}
 * @extends {FinderAction}
 * @extends {FinderFilter}
 */
class Finder extends FinderBase {
  constructor(page, ...args){
    super(page, ...args)
  }
  /**
   * Set "selected" property true on option element whose textContent match the given string.
   * This method is assumed to be called on a select element
   * @return {Promise}
   */
  selectOption(content){
    return this.find("option", FinderFilter.haveContent(content.trim())).select();
  }
  /**
   * Alias of "selectOption"
   * @return {Promise}
   */
  choose(content){
    return this.selectOption(content);
  }
  /**
   * Attach the file given with filePath.
   * This method implemented at FinderExtentions by Browser. so please read api-document of FinderExtentions you use.
   * @param {string} filePath - Specify the path of the file to be attached
   * @param {object} options - options given to FinderExtentions
   * @return {Promise}
   */
  attachFile(filePath, options = {}){
    throw new Error("not implemented")
  }
  /**
   * execute "JavaScript code" given as argument
   * @return {Promise}
   */
  executeJs(code){
    return this.withAction(code).evaluate();
  }
  /**
   * @return {Promise} Promise which resolved with array of finder, that array length is matched number
   */
  toArray(){
    return this.count()
    .then((count)=>{
      let array = [];
      for(let i = 0; i < count; i++){
        array.push(this.clone().find(`(node, idx)=>{ return idx == ${i} }`));
      }
      return array;
    })
  }
  /**
   * return true if all elements which is found by Finder satisfied given filter
   * @example
   * await page.find("input").every(FinderFilter.haveValue("123")) // return true or false
   */
  every(code){
    let filtered = this.clone().find(code);
    return Promise.all([ this.count(), filtered.count() ])
    .then((beforeAndAfter)=>{ return beforeAndAfter[0] == beforeAndAfter[1] })
  }

  /**
   * Make this finder object executed in given iframe.
   * @param {Finder} finderForIframe - finder object for iframe
   * @return {Finder}
   * @example
   *   await page.find("h3").inIframe(page.find("iframe").getFirst()).text()
   */
  inIframe(finderForIframe){
    return this.withContext(finderForIframe.toContextString());
  }
  /**
   * @private
   */
  toContextString(){
    let codeToFindIframeObject = this.clone().withAction(elements => elements).toJsCode();
    return `(()=>{ ${codeToFindIframeObject} })()[0].contentWindow`;
  }
  /**
   * this is same for `page.waitForPageLoad(async ()=>{ await this.click() })`
   */
  clickAndWaitLoading(){
    return this.page.waitForPageLoad(()=>{ this.click() });
  }
  /**
   * this is same for `page.waitForAjaxDone(async ()=>{ await this.click() })`
   */
  clickAndWaitAjaxDone(){
    return this.page.waitForAjaxDone(()=>{ this.click() });
  }
}

function mixin(dest, source){
  for(let key of Reflect.ownKeys(source)){
    if(key === "constructor") continue;

    let descriptor = Object.getOwnPropertyDescriptor(source, key);
    descriptor.configurable = true;
    descriptor.enumerable   = false;
    if(descriptor.hasOwnProperty("writable")){
      descriptor.writable = true;
    }
    Object.defineProperty(dest, key, descriptor);
  }
}

mixin(Finder.prototype, FinderAction.prototype);
mixin(Finder.prototype, FinderFilter.prototype);

module.exports = Finder;

/**
 * This class provides action methods for Finder.
 * This class is Mixined to "Finder" class
 */
class FinderAction {
  static get count(){
    return (elements)=>{ return elements ? elements.length : 0 }
  }
  static get text(){
    return (elements)=>{ return elements && elements[0].textContent }
  }
  static attribute(attrName){
    return `(elements)=>{ return elements && elements[0].attributes["${attrName}"].value }`;
  }
  static setText(text){
    return `(elements)=>{ for(let i = 0; i < elements.length; i++){ elements[i].textContent = "${text}" } }`
  }
  static get value(){
    return (elements)=>{ return elements && elements[0].value }
  }
  static setValue(val){
    return `(elements)=>{ for(let i = 0; i < elements.length; i++){ elements[i].value = "${val}" } }`
  }
  static get click(){
    return (elements)=>{
      if(elements && elements[0]){
        let event = document.createEvent('MouseEvent');
        event.initMouseEvent("click", true, true, window, 0, 0, 0, 0, 0, false, false, false, false, 0, null);
        elements[0].dispatchEvent(event);
      }
    }
  }
  static setChecked(bool){
    return `(elements)=>{ for(let i = 0; i < elements.length; i++){ elements[i].checked = ${bool}; } }`
  }
  static setSelected(bool){
    return `(elements)=>{ elements && elements[0].selected = ${bool} }`
  }

  ////// instance methods //////////////////////////////////////////////////////////////////

  /*
   * Get a textContent of element which is found first by Finder.
   * @return {Promise} Promise resolved with element.textContent
   */
  text(){
    return this.withAction(FinderAction.text).evaluate()
  }
  /**
   * Get a value of element which is found first by Finder.
   * @return {Promise} Promise resolved with element.value
   */
  value(){
    return this.withAction(FinderAction.value).evaluate()
  }
  /**
   * Get a attribute of element which is found first by Finder.
   * @return {Promise} Promise resolved with element.attributes[attrName].value
   */
  attribute(attrName){
    return this.withAction(FinderAction.attribute(attrName)).evaluate()
  }
  /**
   * Click an element which is found first by Finder
   * @return {Promise} Promise resolved after click event
   */
  click(){
    return this.withAction(FinderAction.click).evaluate()
  }
  /**
   * Get a count of elements which is found by Finder.
   * @return {Promise} Promise resolved with matched number
   */
  count(){
    return this.withAction(FinderAction.count).evaluate()
  }
  /**
   * @return {Promise} return true if count of elements which is found by Finder is over one.
   */
  exist(){
    return this.count().then(count => count >= 1)
  }
  /**
   * @return {Promise} return true if count of elements which is found by Finder is zero.
   */
  notExist(){
    return this.exist().then(exist => !exist)
  }
  /**
   * Set given value to value of all elements which is found by Finder
   * @return {Promise} Promise resolved after setting value
   */
  setValue(val){
    return this.withAction(FinderAction.setValue(val)).evaluate()
  }
  /**
   * Alias of "setValue"
   */
  fillIn(val){
    return this.setValue(val)
  }
  /**
   * Set given text to textContent of all elements which is found by Finder
   * @return {Promise} Promise resolved after setting textContent
   */
  setText(text){
    return this.withAction(FinderAction.setText(text)).evaluate()
  }
  /**
   * Set "checked" property true on all elements which is found by Finder
   * @return {Promise}
   */
  check(){
    // TODO target is all elements
    return this.withAction(FinderAction.setChecked(true)).evaluate()
  }
  /**
   * Set "checked" property true on all elements which is found by Finder
   * @return {Promise}
   */
  uncheck(){
    // TODO target is all elements
    return this.withAction(FinderAction.setChecked(false)).evaluate()
  }
  /**
   * Alias of "click"
   * @return {Promise}
   */
  chooseRadioButton(){
    return this.click();
  }
  /**
   * Set "selected" property true on element which is found by Finder
   * @return {Promise}
   */
  select(){
    return this.withAction(FinderAction.setSelected(true)).evaluate();
  }
}

module.exports = FinderAction;

const debug = require("../utils/debug").getLogger("app");

class FinderBase {
  static convertArgumentsToConditions(...args){
    return args.map((arg)=>{
      if(typeof arg == 'function'){
        return new Filter(arg.toString());
      }
      if(typeof arg == 'string'){
        if(arg.match(/\)\s*=>\s*\{/) || arg.trim().match(/^function/)){
          return new Filter(arg);
        }else{
          return new Selector(arg);
        }
      }
      throw `Condition parameter is unexpected value, [${arg}]`;
    })
  }
  constructor(page, ...args){
    this._page = page;
    this._conditions = FinderBase.convertArgumentsToConditions(...args);
    this._allowNotFound = false;
    this._context = undefined;
    this._action = undefined;
  }
  get page(){ return this._page }
  get conditions(){ return this._conditions }
  get action(){ return this._action }
  get context(){ return this._context }
  get allowNotFound(){ return this._allowNotFound }
  allowNotFound(val){ this._allowNotFound = val }
  /**
   * @return {Object} cloned object
   */
  clone(){
    return Object.assign(new this.constructor(this.page), {
      _conditions: this.conditions.concat(),
      allowNotFound: this.allowNotFound,
      _action: this.action,
      _context: this.context
    });
  }
  /**
   * @return {FinderBase} self which have added conditions
   */
  find(...args){
    this._conditions = this.conditions.concat(FinderBase.convertArgumentsToConditions(...args));
    return this;
  }
  withAction(action){
    this._action = typeof action == "function" ?  action.toString() : action;
    return this;
  }
  withContext(context){
    this._context = context;
    return this;
  }
  /**
   * generate and return javascript code for finding element by this finder object
   * @return {string}
   */
  toJsCode(){
    let conditions = this.conditions;
    if(!conditions || !(conditions[0] instanceof Selector)){
      conditions = [new Selector("*")];
    }

    let jsCode = `
      let _document = ${this.context ? this.context : 'window'}.document;
      var elements = Array.from(_document.querySelectorAll("${conditions[0].selector}"));
    `;
    jsCode += conditions.slice(1).map((condition)=>{
      return condition instanceof Selector ?
        `{
          elements = elements.reduce((arr, e, idx)=>{
            return arr.concat(Array.from(e.querySelectorAll("${condition.selector}")));
          }, []);
        }` :
        `{
          let func = ${condition.code};
          elements = elements.filter((e, idx)=>{ return func(e, idx) });
        }`;
    }).join("\n");

    if(!this.allowNotFound){
      jsCode += `
        if(elements.length == 0){
          return Promise.reject('not found');
        }
      `
    }
    if(this.action){
      jsCode += `
        return (${this.action})(elements);
      `;
    }
    else{
      jsCode += `return elements.length;`
    }
    return jsCode;
  }

  /**
   * @return {Promise} Promise resolved in evaluated JavaScript code
   */
  evaluate(){
    let jsCode = this.toJsCode();
    debug(`Evaluate Javascript "${jsCode}"`)

    return this.page.executeJs(`(()=>{ try{ ${jsCode} }catch(e){ return Promise.reject(e.toString()) } })()`)
    .catch((err)=>{
      debug(`Error occurred in Evaluating Javascript: ${err}`);
      throw err
    })
  }
}

class Filter {
  constructor(code){
    this.code = code;
  }
}
class Selector {
  constructor(selector){
    this.selector = selector;
  }
}

module.exports = FinderBase;

class Private {
  static haveContent(content, haveContent = true){
    return `(elm, i)=>{ return elm.textContent && (elm.textContent.trim() == "${content.trim()}") === ${haveContent} }`;
  }
  static haveValue(value, haveValue = true){
    return `(elm, i)=>{ return elm.value && (elm.value.trim() == "${value.trim()}") === ${haveValue} }`;
  }
  static haveClass(classname, haveClass = true){
    return `(elm, i)=>{ return elm && (elm.classList.contains("${classname}")) === ${haveClass} }`;
  }
  static haveStyle(styleName, value, haveStyle = true){
    return `(elm, i)=>{ let style = getComputeStyle(elm); return style && (style["${styleName}"] == "${value}") === ${haveStyle} }`
  }
  static be(propName, bool){
    return `(elm, i)=>{ return elm && elm.${propName} === ${bool} }`;
  }
  static isClickable(){
    return "a,button,input[type=button],input[type=submit]";
  }
  static indexOf(index){
    return `(elm, i)=>{ return i == ${index} }`;
  }
}

/**
 * This class provides filtering methods for Finder.
 * This class is Mixined to "Finder" class
 */
class FinderFilter {
  /**
   * @return {string} return function code to select elements which have given content as element.textContent
   */
  static haveContent(content){
    return Private.haveContent(content, true);
  }
  /**
   * @return {string} return function code to select elements which do not have given content as element.textContent
   */
  static notHaveContent(content){
    return Private.haveContent(content, false);
  }
  /**
  * Alias of haveContent
   * @return {string} return function code to select elements which have given content as element.textContent
   */
  static haveText(text){
    return this.haveContent(text)
  }
  /**
   * Alias of notHaveContent
   * @return {string} return function code to select elements which do not have given content as element.textContent
   */
  static notHaveText(text){
    return this.notHaveContent(text)
  }
  /**
   * @return {string} return function code to select elements which have given value as element.value
   */
  static haveValue(value){
    return Private.haveValue(value, true);
  }
  /**
   * @return {string} return function code to select elements which do not have given value as element.value
   */
  static notHaveValue(value){
    return Private.haveValue(value, false);
  }
  /**
   * @return {string} return function code to select elements which has given value as element.checked attribute.
   */
  static beChecked(bool){
    return Private.be("checked", bool)
  }
  /**
   * @return {string} return function code to select elements which has given value as element.selected attribute.
   */
  static beSelected(bool){
    return Private.be("selected", bool)
  }
  /**
   * @return {string} return function code to select elements which has given value as element.disabled attribute.
   */
  static beDisabled(bool){
    return Private.be("disabled", bool)
  }
  /**
   * @return {string} return function code to select elements which has given value in element.classList attribute.
   */
  static haveClass(classname){
    return Private.haveClass(classname, true);
  }
  /**
   * @return {string} return function code to select elements which do not has given value in element.classList attribute.
   */
  static notHaveClass(classname){
    return Private.haveClass(classname, false);
  }
  /**
   * @return {string} return function code to select elements which has given value in window.getComputedStyle(element)
   */
  static haveStyle(styleName, value){
    return Private.haveStyle(styleName, value, true);
  }
  /**
   * @return {string} return function code to select elements which do not has given value in window.getComputedStyle(element)
   */
  static notHaveStyle(styleName, value){
    return Private.haveStyle(styleName, value, false);
  }

  /**
   * return css selector string to select clickable elements like a, button, input[type=button]
   * @return {string}
   */
  static isClickable(){
    return Private.isClickable()
  }

  /**
   * return function code to select elements which is in given index in elements
   * @return {string}
   */
  static indexOf(i){
    return Private.indexOf(i);
  }

  ////// instance methods /////////////////////////////////////////////////////////////////////

  /**
   * @return {Finder} return filtered Finder object which has the specified value
   */
  haveValue(val){
    return this.find(FinderFilter.haveValue(val))
  }
  /**
   * @return {Finder} return filtered Finder object which does not have the specified value
   */
  notHaveValue(val){
    return this.find(FinderFilter.notHaveValue(val))
  }
  /**
   * @return {Finder} return filtered Finder object which has the specified text
   */
  haveContent(content){
    return this.find(FinderFilter.haveContent(content))
  }
  /**
   * @return {Finder} return filtered Finder object which does not have the specified text
   */
  notHaveContent(content){
    return this.find(FinderFilter.notHaveContent(content))
  }
  /**
   * @return {Finder} return filtered Finder object which has the specified text
   */
  haveText(text){
    return this.find(FinderFilter.haveContent(text))
  }
  /**
   * @return {Finder} return filtered Finder object which does not have the specified text
   */
  notHaveText(text){
    return this.find(FinderFilter.notHaveContent(text))
  }
  /**
   * @return {Finder} return filtered Finder object which has been checked
   */
  beChecked(){
    return this.find(FinderFilter.beChecked(true))
  }
  /**
   * @return {Finder} return filtered Finder object which do not has been checked
   */
  notBeChecked(){
    return this.find(FinderFilter.beChecked(false))
  }
  /**
   * @return {Finder} return filtered Finder object which has been selected
   */
  beSelected(){
    return this.find(FinderFilter.beSelected(true))
  }
  /**
   * @return {Finder} return filtered Finder object which do not has been selected
   */
  notBeSelected(){
    return this.find(FinderFilter.beSelected(false))
  }
  /**
   * @return {Finder} return filtered Finder object which has specified CssClass
   */
  haveCssClass(classname){
    return this.find(FinderFilter.haveClass(classname))
  }
  /**
   * @return {Finder} return filtered Finder object which do not has specified CssClass
   */
  notHaveCssClass(classname){
    return this.find(FinderFilter.notHaveClass(classname))
  }
  /**
   * @return {Finder} return filtered Finder object which has specified CssProperty
   */
  haveCssProperty(propName, value){
    return this.find(FinderFilter.haveStyle(propName, value))
  }
  /**
   * @return {Finder} return filtered Finder object which do not has specified CssProperty
   */
  notHaveCssProperty(propName, value){
    return this.find(FinderFilter.notHaveStyle(propName, value))
  }

  /**
   * @return {Finder} return filtered Finder object which is clickable like a, button, input[type=button]
   */
  isClickable(){
    return this.find(FinderFilter.isClickable())
  }
  /**
   * @return {Finder} return filtered Finder object which is in given index in elements
   */
  indexOf(i){
    return this.find(FinderFilter.indexOf(i));
  }
  /**
   * @return {Finder} return Finder for the first matched element. this is same to "indexOf(0)"
   */
  first(){
    return this.indexOf(0);
  }

}

