/**
 * This is a Mixin object to extend for Page class.
 * Define the Alias method for methods that is defined in Page class, or define any other convinience methods.
 */
let PageMixin = {
  /**
   * When string given, load given URL page. Otherwise, wait for finish page load.
   * This is same for `page.load()` or `page.waitForPageLoad()`
   * @return {Promise}
   */
  waitLoading: function(...args){
    if(typeof args[0] == 'undefined' || typeof args[0] == 'function'){
      return this.waitForPageLoad(...args)
    }else{
      return this.load(...args)
    }
  },
  /**
   * Alias of "waitForXXXX" method.
   * @param {string} type - Type of event for waiting. type string must be waitFor"XXXX".
   * @param {function} action - A function representing the operation of open alert dialog
   * @param {any} args - any arguments for waitFor"XXXX"
   */
  waitFor(type, action, ...args){
    if(!this[`waitFor${type}`]){
      throw new Error('Invalid type: ' + type);
    }
    return this[`waitFor${type}`](action, ...args);
  },
  /**
  * Create and Return new Finder object that is selecting clickable element
  * which is a-tag, button-tag, input[type=button,type=submit]-tag.
  * @return {Finder}
  */
  findClickable: function(){
    return this.find().isClickable()
  },
  // TODO: do you need this method?
  takeScreenshotAuto: function(){
    if(!config.takeScreenshotAuto) return Promise.resolve();

    let now = new Date();
    let datetimeString = now.toLocaleString().replace(/[\-\:]/g, '').replace(/\ /, '_');
    let millsecString = (1000 + now.getMilliseconds()).toString().substr(1);
    filename = `${datetimeString}_${millsecString}.png`
    return this.takeScreenshot(path.join(config.screenshotsDir, filename));
  }
}

module.exports = PageMixin;

const EventEmitter = require('events');
const path = require('path');
const {config} = require('../utils/config');
const Page = require('./page');
const pageWalker = require('../page_walker');

class Window extends EventEmitter {
  constructor(id, browser, browserWindow, options = {}){
    super();
    this.id = id;

    let configCloned = config.clone();
    configCloned.merge({ [config.browser]: options });

    let windowSetupAfterReady = ()=>{
      pageWalker.browser.createBrowserWindow(configCloned[config.browser])
      .then((createdBrowserWindow)=>{
        this._browserWindow = createdBrowserWindow;
        this.emit('readyForPage');
      });
    };

    // if browserWindow has been ready
    if(browserWindow){
      this._browserWindow = browserWindow;
      this.emit('readyForPage');
      return;
    }

    // if Browser has been ready
    if(browser && browser.isReady()){
      windowSetupAfterReady();
    }else{
      browser.on('ready', windowSetupAfterReady);
    }
  }
  /**
   * @return {object} BrowserWindow Object(Provided by Each Browser)
   * @see Browser#createWindow
   */
  get browserWindow(){ return this._browserWindow }
  /**
   * close this BrowserWindow();
   */
  close(){
    // Each browserWindow object must have close() method.
    this._browserWindow.close();
  }
  /**
   * @private
   */
  isReadyForPage(){ return !!this._browserWindow }
  /**
   * get Page object in this window.
   * @return {Page}
   */
  get page(){
    if(!this._page){
      this._page = new Page(pageWalker.browser.createBrowserPage(this));
    }
    return this._page;
  }
}

module.exports = Window;

const electron = require('electron');
const uuidv4 = require('uuid').v4;
const Logger = require('./utils/logger');
const LoadWalkerModule = ()=>{ return require('./walker/walker') }; // for Lazy loading
const LoadWindowModudle = ()=>{ return require('./page/window') }; // for Lazy loading
const Browser = require('./browser/interface/browser');

class PageWalker {
  constructor(){
    this.windows = {};
  }
  initialize(config){
    if(this.config) return; // already initialized.

    const Walker = LoadWalkerModule();
    Logger.initialize(config);

    this._config = config;
    this._browser = new (require(`./browser/${config.browser}`))(config[config.browser]);
    this._walker = new Walker(config);
  }
  start(config){
    this.initialize(config);

    // Attach event handler to browser.
    this._browser.on(Browser.Events.Ready, ()=>{
      this._walker.walk((failures)=>{
        console.log(`[${failures == 0? 'SUCCESS': 'FAILURE'}!] finished with status:${failures}`);
        if(this.config.exitImmediately){
          this._browser.exit(failures);  // exit with non-zero status if there were failures
        }else{
          Object.values(this.windows).forEach((obj)=>{ obj.window.page.openDevTools() });;
        }
      })
    });
    this._browser.on(Browser.Events.NewWindow, (browserWindow, name)=>{
      let windowId = this.createWindow(name, browserWindow, {})
      Object.defineProperty(browserWindow, 'pwWindowUUID', { value: windowId, writable: false });
    });
    this._browser.on(Browser.Events.Closed, (browserWindow)=>{
      this.removeWindow(browserWindow.pwWindowUUID);
    });
  }
  /**
   * @return {Browser} Browser Object(Instance will be relayed on Browser used)
   */
  get browser(){
    return this._browser;
  }
  get config(){
    return this._config;
  }
  get page(){
    if(!this.__defaultWindowId__){
      this.__defaultWindowId__ = this.createWindow("__default__", undefined, { title: "MainWindow - PageWalker" });
    }
    return this.getWindowById(this.__defaultWindowId__).page;
  }
  getWindowById(id){
    return this.windows[id] && this.windows[id].window;
  }
  getWindowByBrowserWindow(browserWindow){
    return this.getWindowById(browserWindow.pwWindowUUID);
  }
  getWindowByName(){
    return this.getWindowById(this.getWindowIdByName(name));
  }
  createWindow(name, browserWindow, windowOptions = {}){
    const Window = LoadWindowModudle();
    const id = uuidv4();
    this.windows[id] = {
      name: name,
      window: new Window(id, this._browser, browserWindow, windowOptions),
    }
    return id;
  }
  /**
   * remove window referrence, but not destroy window object.
   */
  removeWindow(id){
    delete this.windows[id];
  }
  /**
   *
   */
  getWindowIdByName(name){
    return Object.keys(this.windows).find(id => this.windows[id] && this.windows[id].name == name);
  }
}

module.exports = new PageWalker();

const fs = require("fs");
const path = require("path");
const OptParser = require("./opt_parser");

/**
 * this class have configuratin information.
 */
class Config {
  static get CONFIG_FILE_PATH(){
    return "config.json";
  }
  static get DEFAULT_CONFIG(){
    return {
      browser: "electron",
      scenarioDir: "test",
      scenarioFile: undefined,
      ignoreDir: "supports",
      silentMode: false,
      exitImmediately: false,  // whether or not keep shows for browser window on failed
      takeScreenshotAuto: false,
      fileDownloadDir: "downloads",
      screenshotsDir: "screenshots",
      width: 1024,
      height: 768,
      mocha: {
        ui: "bdd",
        bail: true,
        reporter: "spec",
        timeout: 5000,
        slow: false,
      },
      assertion: {
        png: {
          threshold: 0.3,
          ksize: 50
        }
      },
      logger: {
        appenders: {
          out: { type: 'stdout' }
        },
        categories: {
          default: { appenders: ['out'], level: 'debug' },
        },
      },
      electron: {
        show: true,
        webPreferences: {
          nodeIntegration: false,
          preload: path.join(__dirname, "../browser/electron/window_preloaded.js"),
          allowRunningInsecureContent: true
        }
      },
      puppeteer: {
        product: 'chrome',
        headless: false
      },
    }
  }
  static create(commandLineArgs){
    this.instance = new Config();
    this.instance.merge(Config.DEFAULT_CONFIG)
    this.instance.loadConfigFile(this.CONFIG_FILE_PATH);
    this.instance.loadConfigFile(this.CONFIG_FILE_PATH + ".local");

    let optParser = new OptParser(commandLineArgs);
    this.instance.merge(optParser.parse((config, cmdOpt)=>{
      switch(cmdOpt.option()){
        case "-s":
        case "--silent":          config.silentMode     = true; break;
        default:
          let optionKey = cmdOpt.option(true).replace(/--/, '');
          if(this.instance.isExists(optionKey)){
            Config.assign(config, optionKey, cmdOpt.getValueOrFail())
          }else{
            const msg = "Unexpected CommandLine Argument, [" + cmdOpt.option() + "]"
            console.log(msg);
            throw new Error(msg);
          }
      }
    }));

    optParser.scriptArguments().forEach((args)=>{
      let filePath = args[0] == "/" ? args : path.join(process.cwd(), args);
      let stat = fs.statSync(filePath)
      let keyName = stat.isDirectory() ? "scenarioDir" : "scenarioFile";
      if(!(this.instance[keyName] instanceof Array)){
        this.instance[keyName] = []
      }
      this.instance[keyName].push(args);
    })

    // apply options
    if(this.instance.silentMode){
      this.instance.electron.show = false;
      this.instance.puppeteer.headless = true;
      this.instance.mocha.reporter = "dot";
      this.instance.exitImmediately = true;
    }
    this.instance.puppeteer.defaultViewport = this.instance.puppeteer.defaultViewport || {};
    this.instance.electron.width = this.instance.puppeteer.defaultViewport.width = this.instance.width;
    this.instance.electron.height = this.instance.puppeteer.defaultViewport.height = this.instance.height;

    // validates
    if(!["electron", "puppeteer"].includes(this.instance.browser)){
      throw new Error(`config.browser is not allowed! [${this.instance.browser}]`)
    }
    return this.instance;
  }
  static get config(){
    return this.instance;
  }
  /**
   * @private
   */
  loadConfigFile(filePath){
    if(fs.existsSync(filePath)){
      this.merge(JSON.parse(fs.readFileSync(filePath, 'utf8')));
    }
  }
  clone(){
    let cloned = new Config();
    cloned.merge(this);
    return cloned;
  }
  merge(source){
    Config.merge(this, source);
  }
  static merge(dest, source){
    Object.keys(source).forEach((key)=>{
      if(typeof source[key] == "object" && typeof dest[key] == "object"){
        this.merge(dest[key], source[key]);
      }else{
        dest[key] = source[key];
      }
    });
  }
  isExists(key){
    let parentObj = this;
    return !!key.split(".").every((k)=>{
      let isExists = parentObj && (k in parentObj);
      parentObj = isExists && parentObj[k];
      return isExists;
    })
  }
  static isExists(object, key){
    if(typeof object != "object") return false;
    if(key in object){
      return typeof object[key] == "object" ? object[key] : true;
    }
  }
  static assign(config, key, value){
    let keys = key.split(".");
    keys.reduce((object, key, index)=>{
      if(index < keys.length - 1){
        if(!object[key]){
          object[key] = {}
        }
        return object[key];
      }
      switch(typeof object[key]){
        case 'number':  object[key] = new Number(value);
        case 'boolean': object[key] = new Boolean(value);
        default:        object[key] = value;
      }
    }, config);
  }
}

module.exports = Config;

class DateFormat {
  static format(date, formatString){
    let [ yyyy, mm, dd, HH, MM, ss, SS ] = date.toISOString().split(/[-T:.]/);
    let replacement = { yyyy: yyyy, mm: mm, dd: dd, HH: HH, MM: MM, ss: ss, SS: SS };

    for(let key in replacement){
      formatString = formatString.replace(new RegExp(key), replacement[key])
    }
    return formatString;
  }
  static toTimepstampString(date){
    return this.format(date, 'yyyymmddHHMMss')
  }
  static toTimepstampMsecString(date){
    return this.format(date, 'yyyymmddHHMMssSS')
  }
}

module.exports = DateFormat;

const log4js = require ('log4js');

/**
 * Usage:
 *   const Logger = require('path/to/logger');
 *
 *   Logger.info "message"   (alias of Logger.default.info)
 *   Logger.category.info "message"   ("category" must be defined on log4js settings)
 */
class Logger {

  static trace(str){
    Logger.defaultLogger().trace(str);
  }
  static debug(str){
    Logger.defaultLogger().debug(str);
  }
  static warn(str){
    Logger.defaultLogger().warn(str);
  }
  static info(str){
    Logger.defaultLogger().info(str);
  }
  static error(str){
    Logger.defaultLogger().error(str);
  }
  static fatal(str){
    Logger.defaultLogger().fatal(str);
  }

  static initialize(config){
    log4js.configure(config.logger || {});
    if(config.logger && config.logger.categories && typeof config.logger.categories){
      Object.keys(config.logger.categories).forEach((category)=>{
        this[category] = log4js.getLogger(category);
      })
    }
  }

  static defaultLogger(){
    if(this.default){
      return this.default;
    }
    // return defaultLogger if not initialized.
    if(!this.__defaultLogger__){
      this.__defaultLogger__ = new Logger();
    }
    return this.__defaultLogger__;
  }

  trace(str){
    console.log(`TRACE: #{str}`);
  }
  debug(str){
    console.log(`DEBUG: #{str}`);
  }
  warn(str){
    console.log(`WARN: #{str}`);
  }
  info(str){
    console.log(`INFO: #{str}`);
  }
  error(str){
    console.log(`ERROR: #{str}`);
  }
  fatal(str){
    console.log(`FATAL: #{str}`);
  }
}

module.exports = Logger;

function mixin(dest, source){
  for(let key of Reflect.ownKeys(source)){
    if(key === "constructor") continue;

    let descriptor = Object.getOwnPropertyDescriptor(source, key);
    descriptor.configurable = true;
    descriptor.enumerable   = false;
    if(descriptor.hasOwnProperty("writable")){
      descriptor.writable = true;
    }
    Object.defineProperty(dest, key, descriptor);
  }
}

module.exports = mixin;

class CommandLineOption {
  constructor(optString){
    this._option = optString;
  }
  option(removeHyphen = false){
    if(removeHyphen){
      return this._option.slice(this._option[1] == "-" ? 2 : 1);
    }
    return this._option;
  }
  setValue(val){ this._value = val }
  getValueOrFail(){
    if(this._value == undefined) throw new Error("CommandLine Argument [" + this.option() + "] is missing.");
    this.valueIsOption = true;
    return this._value;
  }
}

class OptParser {
  constructor(commandLineArgs){
    this._commandLineOptions = [];
    this._scriptArguments = [];
    commandLineArgs.forEach((arg, i)=>{
      if(arg[0] == "-"){
        this._commandLineOptions.push(new CommandLineOption(arg));
      }else{
        if(i > 0 && commandLineArgs[i-1][0] == "-"){
          let commandLineOption = this._commandLineOptions[this._commandLineOptions.length - 1];
          commandLineOption.setValue(arg)
          this._scriptArguments.push(commandLineOption);
        }else{
          this._scriptArguments.push(arg);
        }
      }
    });
  }
  parse(callback){
    return this._commandLineOptions.reduce((result, cmdOpt, index, array)=>{
      callback(result, cmdOpt, index, array);
      return result;
    }, {});
  }
  scriptArguments(){
    return this._scriptArguments.filter(v => !(v instanceof CommandLineOption && v.valueIsOption))
    .map(v => (v instanceof CommandLineOption ? v._value : v))
  }
}

module.exports = OptParser;

const fs = require("fs");
const {promisify} = require('util');
const {config} = require("../utils/config");

class ResultStore {
  // ファイルから前回の結果をロードし、それを格納した配列で解決されるPromiseを返す
  static loadPreviousResults(){
    if(!fs.existsSync(config.successSaveFile))
      return Promise.resolve([]);

    return new Promise((resolve, reject)=>{
      fs.readFile(config.successSaveFile, 'utf-8', (err, data)=>{ err ? reject(err) : resolve(data) })
    })
    .then((data)=>{
      return data.split("\n")
    })
  }
  // すべてのテストが成功した場合に、すべてのテストタイトルをファイルに書き出す
  // ファイル書込みが終わると解決するPromiseを返す
  static saveAll(suite){
    return this.write(suite, []);
  }
  // 失敗したときに呼ばれる結果保存メソッド
  // failtest までのテストを保存する
  // ファイル書込みが終わると解決するPromiseを返す
  static saveSucceeded(suite, failtest){
    let noSucceeded = [];
    let failedTitle = failtest.fullTitle().replace(/"before each" hook for "(.+)"/, "$1");
    suite.eachTest((test)=>{
      if(noSucceeded.length > 0 || test.fullTitle() == failedTitle){
        noSucceeded.push(test.fullTitle());
      }
    });
    if(noSucceeded.length > 0){
      return this.write(suite, noSucceeded);
    }
  };

  /**
   * 引数で指定された noSucceeded 以外のテスト名をファイルに書き込む
   * @private:
   */
  static write(suite, noSucceeded){
    let succeededs = [];
    suite.eachTest((test)=>{
      if(noSucceeded.indexOf(test.fullTitle()) == -1)
        succeededs.push(test.fullTitle())
    })
    return new Promise((resolve, reject)=>{
      fs.writeFile(config.successSaveFile, succeededs.join("\n")+"\n", (err)=>{
        err ? reject(err) : resolve();
      })
    })
  }
}

module.exports = ResultStore;

const Mocha = require('mocha');
const fs = require('fs');
const path = require('path');
const ResultStore = {} // require("./result_store");
const mkdirp = require("mkdirp");

class Walker {
  constructor(config){
    this.config = config;
    this.mocha = new Mocha(Object.assign({}, config.mocha, { timeout: config.mocha.timeout * 2 }));

    if(this.config.screenshotsDir){
      mkdirp.sync(path.join(this.config.screenshotsDir, 'actual'));
      mkdirp.sync(path.join(this.config.screenshotsDir, 'expected'));
    }

    if(this.config.scenarioFile){
      let scenarioFiles = Walker.toArray(this.config.scenarioFile);
      scenarioFiles.sort().forEach((file)=>{ this.mocha.addFile(file) })
    }else{
      let scenarioDirs = Walker.toArray(this.config.scenarioDir);
      let ignoreDirs = Walker.toArray(this.config.ignoreDir);
      scenarioDirs.map(dir => path.join(process.cwd(), dir)).forEach((scenarioDir)=>{
        if(!Walker.isDir(scenarioDir)){
          throw `Scenario Directory(${scenarioDir}) is not found or not directory!`;
        }
        Walker.findAllFilesSync(scenarioDir, ignoreDirs)
        .filter(file => file.substr(-3) === '.js')
        .forEach((file)=>{ this.mocha.addFile(file) })
      })
    }
    this.loadFiles();
  }
  walk(callback){
    this.mocha.run(callback);
  }
  static isDir(path){
    let stat = fs.statSync(path);
    return stat && stat.isDirectory();
  }
  static findAllFilesSync(dir, ignorePattern){
    let results = [];
    fs.readdirSync(dir)
    .filter(file => !ignorePattern.includes(file))
    .map(file => path.join(dir, file))
    .forEach((file)=>{
      if (this.isDir(file)){
        results = results.concat(Walker.findAllFilesSync(file, ignorePattern))
      }else{
        results.push(file)
      }
    })
    return results
  }
  static toArray(value){
    return (value instanceof Array) ? value : [ value ];
  }

  /**
   * @private
   */
  loadFiles(){
    let originalFunctions = {};
    let delayedFunctions = [];
    this.mocha.suite.on('pre-require', (context)=>{
      originalFunctions.describe = context.describe;
      originalFunctions.xdescribe = context.xdescribe;
      originalFunctions.describeOnly = context.describe.only;
      // override
      context.describe = function(title, fn, ...args){
        delayedFunctions.push(["describe", this, title, fn, args]);
      }
      context.xdescribe = function(title, fn, ...args){
        delayedFunctions.push(["xdescribe", this, title, fn, args]);
      }
      context.describe.only = function(title, fn, ...args){
        delayedFunctions.push(["describeOnly", this, title, fn, args]);
      }
      // set to aliases
      context.context = context.suite = context.describe;
      context.xcontext = context.describe.skip = context.xdescribe;
    })
    this.mocha.suite.on('post-require', (context)=>{
      /* Do not support! **
      // restore desctibe, xdescribe, describe.only with object property function support
      // object property function support is describe("title", { "title": function(){...} }, ...args)
      ["describe", "xdescribe", "describeOnly"].forEach((key)=>{
        originalFunctions[key] = Walker.objectPropertySupport(originalFunctions[key]);
      })
      */
      // Revert override.
      context.context = context.suite = context.describe = originalFunctions.describe;
      context.xcontext = context.xdescribe = originalFunctions.xdescribe;
      context.describe.only = originalFunctions.describeOnly;
      /* Do not support! **
      // hook function allows object property support.
      ["before", "after", "beforeEach", "afterEach"].filter(key => context[key]).forEach((key)=>{
        context[key] = Walker.objectPropertySupport(context[key]);
      })
      */
    })
    this.mocha.loadFiles();
    // Delayed evaluation.
    delayedFunctions.forEach((array)=>{
      let func = originalFunctions[array[0]];
      func.call(array[1], array[2], array[3], ...array[4]); // this, title, fn, args
    })
  }
  /**
   * @deprecated
   * @private
   */
  static objectPropertySupport(originalFunc){
    let newFunc = function(title, fn, ...args){
      if(typeof title == "function"){
        originalFunc.call(this, title);
      }
      if(typeof fn == "function"){
        originalFunc.call(this, title, fn);
      }
      if(typeof fn == "object"){
        if(typeof fn[title] != "function") throw new Error(`Property missing! "${title}" on object [${fn}]`)
        originalFunc.call(this, title, function(){ return fn[title].call(this, ...args) });
      }
    };
    for(var prop in originalFunc){
      if(originalFunc.hasOwnProperty(prop)) newFunc[prop] = originalFunc[prop];
    }
    return newFunc;
  }
  /**
   * @deprecated
   */
  _walk_(callback){
    Promise.resolve().then(()=>{
      return this.config.startFromFirst ? [] : ResultStore.loadPreviousResults();
    })
    .then((succeeded)=>{
      if(succeeded && succeeded.length > 0){
        let regexp = succeeded.map(s => s.replace(/[()|]/g, "\\$&")).join("|");
        this.mocha.options.grep = new RegExp("^("+ regexp +")$");
        this.mocha.options.invert = true;
      }
    })
    .then(()=>{
      let runner = this.mocha.run((failures)=>{
        if(failures==0){
          ResultStore.saveAll(this.mocha.suite).then(()=>{ callback(failures) })
        }else{
          callback(failures);
        }
      });
      runner.on('fail', (failtest, error)=>{
        ResultStore.saveSucceeded(this.mocha.suite, failtest);
      })
    })
  }
}

module.exports = Walker;

